custom_css.post_content:
/*** Demo only */

/* Darken WC product imgs for contrast */
.single-product .product img,
.products .product img {filter:brightness(95%)}
.woocommerce span.onsale,
.woocommerce ul.products li.product .onsale {z-index:5}

/* Home Page Button Spacing */
a.btn-1.btn.th-btn.btn-standard-primary {
    margin-right: 8px;
}

/*** Add to theme */

h1 {font-size:56px}

.elementor-widget-themo-service-block.elementor-position-left .th-service-block-w .elementor-icon-box-icon {
    margin-right: 20px;
}

.elementor-widget-themo-service-block.elementor-position-right .th-service-block-w .elementor-icon-box-icon {
    margin-left: 20px;
}

.flexslider .slides .btn-image img {display:inline}

#main-flex-slider .slides .btn-image {padding:0 !important}

section.th-parallax:not(.th-mobile) {
   background: none !important;
} 

/* Hide Related Products from home */

.page-id-171 .woocommerce .woocommerce-tabs, .page-id-171 .woocommerce .related.products {
    display: none;
}

/* Hide Related Products from Laptop home */

.page-id-400 .woocommerce .woocommerce-tabs, .page-id-400 .woocommerce .related.products {
    display: none;
}

/* Show product title on homepage Woo Shortcode */

.woocommerce div.product .product_title {
display:inherit;
}

/* video_play shortcode */

.icon-light {
    color: #fff;
}
.icon-large{
font-size: 90px;
}
